const fs = require('fs');
const metadata = JSON.parse(fs.readFileSync('app_metadata.json', 'utf8'));

// Generate TypeScript
let types = `// Auto-generated TypeScript types for LivingApps\n\n`;
types += `export const APP_IDS = {\n`;

for (const [key, app] of Object.entries(metadata.apps)) {
  types += `  ${key.toUpperCase()}: "${app.app_id}",\n`;
}

types += `} as const;\n\n`;

for (const [key, app] of Object.entries(metadata.apps)) {
  const typeName = key.charAt(0).toUpperCase() + key.slice(1);
  types += `export interface ${typeName} {\n`;
  types += `  record_id: string;\n`;
  types += `  fields: {\n`;

  for (const [fieldKey, field] of Object.entries(app.controls)) {
    let tsType = 'string | null';
    if (field.type === 'number') tsType = 'number | null';
    else if (field.type === 'bool') tsType = 'boolean | null';
    else if (field.type === 'date') tsType = 'string | null';
    else if (field.type === 'applookup') tsType = 'string | null';

    types += `    ${fieldKey}: ${tsType};\n`;
  }

  types += `  };\n`;
  types += `}\n\n`;
}

fs.mkdirSync('src/types', { recursive: true });
fs.writeFileSync('src/types/app.ts', types);

// Generate Service
let service = `// Auto-generated LivingApps Service\n\n`;
service += `import { APP_IDS } from '@/types/app';\n`;
service += `import type { ${Object.keys(metadata.apps).map(k => k.charAt(0).toUpperCase() + k.slice(1)).join(', ')} } from '@/types/app';\n\n`;

service += `const API_BASE = 'https://my.living-apps.de/rest';\n\n`;

service += `export function extractRecordId(url: string | null): string | null {
  if (!url) return null;
  const match = url.match(/\\/records\\/([^\\/]+)$/);
  return match ? match[1] : null;
}\n\n`;

service += `export function createRecordUrl(appId: string, recordId: string): string {
  return \`\${API_BASE}/apps/\${appId}/records/\${recordId}\`;
}\n\n`;

service += `async function fetchApi<T>(url: string, options?: RequestInit): Promise<T> {
  const res = await fetch(url, options);
  if (!res.ok) throw new Error(\`API error: \${res.status}\`);
  return res.json();
}\n\n`;

service += `export const LivingAppsService = {\n`;

for (const [key, app] of Object.entries(metadata.apps)) {
  const typeName = key.charAt(0).toUpperCase() + key.slice(1);
  const methodName = key.charAt(0).toUpperCase() + key.slice(1);

  service += `  // ${app.name}\n`;
  service += `  get${methodName}: () => fetchApi<${typeName}[]>(\`\${API_BASE}/apps/\${APP_IDS.${key.toUpperCase()}}/records\`),\n`;
  service += `  create${methodName}: (data: ${typeName}['fields']) => fetchApi<${typeName}>(\`\${API_BASE}/apps/\${APP_IDS.${key.toUpperCase()}}/records\`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ fields: data }) }),\n`;
  service += `  update${methodName}: (id: string, data: Partial<${typeName}['fields']>) => fetchApi<${typeName}>(\`\${API_BASE}/apps/\${APP_IDS.${key.toUpperCase()}}/records/\${id}\`, { method: 'PATCH', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ fields: data }) }),\n`;
  service += `  delete${methodName}: (id: string) => fetchApi<void>(\`\${API_BASE}/apps/\${APP_IDS.${key.toUpperCase()}}/records/\${id}\`, { method: 'DELETE' }),\n\n`;
}

service += `};\n`;

fs.mkdirSync('src/services', { recursive: true });
fs.writeFileSync('src/services/livingAppsService.ts', service);

console.log('Generated types and service');
EOF
